package br.com.vcampanholi.api.config;import br.com.vcampanholi.exception.GenericException;import com.fasterxml.jackson.annotation.JsonInclude;import com.fasterxml.jackson.databind.ObjectMapper;import com.fasterxml.jackson.datatype.jsr310.JavaTimeModule;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.http.HttpStatus;import org.springframework.stereotype.Component;@Componentpublic class ObjectMapperConfig {    private static final Logger LOGGER = LoggerFactory.getLogger(ObjectMapperConfig.class);    public static String convertToString(final Object obj) {        try {            ObjectMapper mapper = new ObjectMapper();            mapper.setSerializationInclusion(JsonInclude.Include.NON_NULL);            mapper.registerModule(new JavaTimeModule());            return mapper.writeValueAsString(obj);        } catch (Exception e) {            LOGGER.error(String.valueOf(e));            throw new GenericException(e.getMessage(), HttpStatus.INTERNAL_SERVER_ERROR);        }    }}