package br.com.vcampanholi.api.config;import com.fasterxml.jackson.annotation.JsonInclude;import com.fasterxml.jackson.databind.ObjectMapper;import com.fasterxml.jackson.datatype.jsr310.JavaTimeModule;@lombok.Generatedpublic class ObjectMapperConverter {    public static String convertToString(final Object obj) {        try {            ObjectMapper mapper = new ObjectMapper();            mapper.setSerializationInclusion(JsonInclude.Include.NON_NULL);            mapper.registerModule(new JavaTimeModule());            return mapper.writeValueAsString(obj);        } catch (Exception e) {            throw new RuntimeException(e);        }    }}