package br.com.vcampanholi.api.v1.sessao;import br.com.vcampanholi.api.v1.sessao.model.request.SessaoRequest;import br.com.vcampanholi.api.v1.sessao.model.response.SessaoResponse;import br.com.vcampanholi.domain.sessao.service.SessaoService;import io.swagger.annotations.*;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import javax.validation.Valid;@RestController@RequestMapping("sessao-associado/v1/pauta")@Api("Serviço para operações na sessão de votação.")public class SessaoController {    private SessaoService sessaoService;    public SessaoController(SessaoService sessaoService) {        this.sessaoService = sessaoService;    }    @PostMapping("/{pauta_id}/sessao")    @ApiOperation(value = "Cria uma sessão para a pauta.", response = SessaoResponse.class)    @ApiResponses(value = {            @ApiResponse(code = 200, message = "OK", response = SessaoResponse.class),    })    public ResponseEntity<SessaoResponse> criarSessao(@ApiParam(value = "Identificador da pauta.", required = true)                                                      @PathVariable("pauta_id") Long pautaId,                                                      @ApiParam(value = "Informações da sessão.", required = true)                                                      @Valid @RequestBody SessaoRequest sessaoRequest) {        return ResponseEntity.status(HttpStatus.CREATED)                .body(sessaoService.criarSessao(pautaId, sessaoRequest));    }}