package br.com.vcampanholi.integration.restclient;import br.com.vcampanholi.integration.model.UserResponse;import br.com.vcampanholi.integration.model.UserStatusEnum;import lombok.AllArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Component;import org.springframework.web.client.HttpStatusCodeException;import org.springframework.web.client.RestTemplate;@Component@AllArgsConstructor@Slf4jpublic class UserRestClient {    private RestTemplate restTemplate;    private static final String URI = "https://user-info.herokuapp.com/users/";    public UserResponse buscarUsuario(String cpf) {        String url = URI.concat(cpf);        try {            return restTemplate.getForEntity(url, UserResponse.class).getBody();        } catch (HttpStatusCodeException exception) {            log.info(exception.getMessage());            return UserResponse.builder().status(UserStatusEnum.INVALID).build();        }    }}