package br.com.vcampanholi.integration.restclient;import br.com.vcampanholi.integration.model.UserResponse;import br.com.vcampanholi.integration.model.UserStatusEnum;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.stereotype.Component;import org.springframework.web.client.HttpStatusCodeException;import org.springframework.web.client.RestTemplate;@Componentpublic class UserRestClient {    private static final Logger LOGGER = LoggerFactory.getLogger(UserRestClient.class);    private RestTemplate restTemplate;    private static final String URI = "https://user-info.herokuapp.com/users/";    public UserResponse buscarUsuario(String cpf) {        String url = URI.concat(cpf);        try {            return restTemplate.getForEntity(url, UserResponse.class).getBody();        } catch (HttpStatusCodeException exception) {            LOGGER.info(exception.getMessage());            return new UserResponse(UserStatusEnum.INVALID);        }    }}