package br.com.vcampanholi.queue.config;import org.springframework.amqp.core.Queue;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class QueueConfig {    @Value("${queue.pauta.computada}")    private String pautaQueue;    @Bean    public Queue queue() {        return new Queue(pautaQueue, true);    }}