package br.com.vcampanholi.queue.send;import lombok.AllArgsConstructor;import org.springframework.amqp.core.Queue;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.stereotype.Component;@Component@AllArgsConstructorpublic class OrderQueueSender {    private RabbitTemplate rabbitTemplate;    private Queue queue;    public void send(String pauta) {        rabbitTemplate.convertAndSend(this.queue.getName(), pauta);    }}