package br.com.vcampanholi.api.v1.pauta.facade;import br.com.vcampanholi.api.v1.stubs.PautaEntityStub;import br.com.vcampanholi.api.v1.stubs.PautaRequestStub;import br.com.vcampanholi.api.v1.stubs.PautaResponseStub;import br.com.vcampanholi.domain.pauta.service.PautaService;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.mockito.InjectMocks;import org.mockito.Mock;import org.springframework.test.context.junit.jupiter.SpringExtension;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.mockito.ArgumentMatchers.any;import static org.mockito.Mockito.when;@ExtendWith(SpringExtension.class)class PautaFacadeTest {    @Mock    private PautaService pautaService;    @InjectMocks    private PautaFacade pautaFacade;    @Test    void deveRetornarUmaPautaGravada() {        when(pautaService.criarPauta(any())).thenReturn(PautaEntityStub.entityToReturn());        var response = pautaFacade.criarPauta(PautaRequestStub.pautaRequest());        assertEquals(response, PautaResponseStub.pautaResponse());    }    @Test    void deveRetornarUmaPautaAoBuscar() {        when(pautaService.buscarPauta(any())).thenReturn(PautaEntityStub.entityToReturn());        assertEquals(pautaFacade.buscarPauta(1L), PautaResponseStub.pautaResponse());    }}