package br.com.vcampanholi.api.v1.sessao.facade;import br.com.vcampanholi.domain.sessao.service.SessaoService;import br.com.vcampanholi.stubs.SessaoEntityStub;import br.com.vcampanholi.stubs.SessaoRequestStub;import br.com.vcampanholi.stubs.SessaoResponseStub;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.mockito.InjectMocks;import org.mockito.Mock;import org.springframework.test.context.junit.jupiter.SpringExtension;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.mockito.ArgumentMatchers.any;import static org.mockito.Mockito.when;@ExtendWith(SpringExtension.class)class SessaoFacadeTest {    @Mock    private SessaoService sessaoService;    @InjectMocks    private SessaoFacade sessaoFacade;    @Test    void deveRetornarUmaSessaoGravada() {        when(sessaoService.criarSessao(any(), any())).thenReturn(SessaoEntityStub.entityToReturn());        var response = sessaoFacade.criarSessao(1L, SessaoRequestStub.sessaoRequest());        assertEquals(response, SessaoResponseStub.sessaoResponse());    }}