package br.com.vcampanholi.api.v1.sessaovoto;import br.com.vcampanholi.domain.sessaovoto.service.SessaoVotoService;import br.com.vcampanholi.stubs.request.SessaoVotoRequestStub;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.web.servlet.WebMvcTest;import org.springframework.boot.test.mock.mockito.MockBean;import org.springframework.http.MediaType;import org.springframework.test.context.junit.jupiter.SpringExtension;import org.springframework.test.web.servlet.MockMvc;import static br.com.vcampanholi.api.config.ObjectMapperConfig.convertToString;import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.post;import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;@ExtendWith(SpringExtension.class)@WebMvcTest(SessaoVotoController.class)class SessaoVotoControllerTest {    @Autowired    private MockMvc mockMvc;    @MockBean    private SessaoVotoService sessaoVotoService;    @Test    void deveCriarUmVotoNaSessao() throws Exception {        this.mockMvc.perform(post("/sessao-associado/v1/pauta/1/sessao/1/voto-sessao")                .contentType(MediaType.APPLICATION_JSON)                .content(convertToString(SessaoVotoRequestStub.sessaoVotoRequest()))                .accept(MediaType.APPLICATION_JSON))                .andExpect(status().isCreated());    }}