package br.com.vcampanholi.domain.pauta.mapper;import br.com.vcampanholi.api.v1.stubs.PautaEntityStub;import br.com.vcampanholi.api.v1.stubs.PautaRequestStub;import br.com.vcampanholi.api.v1.stubs.PautaResponseStub;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.test.context.junit.jupiter.SpringExtension;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNull;@ExtendWith(SpringExtension.class)class PautaMapperTest {    @Test    void deveRetornarNullSeParametroNaoForInformado() {        assertNull(PautaMapper.mapToEntity(null));        assertNull(PautaMapper.mapToResponse(null));    }    @Test    void deveRetornarUmaEntidadeParaPersistencia() {        var entity = PautaMapper.mapToEntity(PautaRequestStub.pautaRequest());        assertEquals(entity, PautaEntityStub.entityToSave());    }    @Test    void deveRetornarUmaEntidadeParaRetornar() {        var response = PautaMapper.mapToResponse(PautaEntityStub.entityToReturn());        assertEquals(response, PautaResponseStub.pautaResponse());    }}