package br.com.vcampanholi.domain.sessao.mapper;import br.com.vcampanholi.stubs.SessaoEntityStub;import br.com.vcampanholi.stubs.SessaoRequestStub;import br.com.vcampanholi.stubs.SessaoResponseStub;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.test.context.junit.jupiter.SpringExtension;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNull;@ExtendWith(SpringExtension.class)class SessaoMapperTest {    @Test    void deveRetornarNullSeParametroNaoForInformado() {        assertNull(SessaoMapper.mapToEntity(null));        assertNull(SessaoMapper.mapToResponse(null));    }    @Test    void deveRetornarUmaEntidadeParaPersistencia() {        var entity = SessaoMapper.mapToEntity(SessaoRequestStub.sessaoRequest());        assertEquals(entity, SessaoEntityStub.entityToSave());    }    @Test    void deveRetornarUmaEntidadeComAResponse() {        var response = SessaoMapper.mapToResponse(SessaoEntityStub.entityToReturn());        assertEquals(response, SessaoResponseStub.sessaoResponse());    }}