package br.com.vcampanholi.domain.sessaovoto.mapper;import br.com.vcampanholi.stubs.entity.SessaoVotoEntityStub;import br.com.vcampanholi.stubs.request.SessaoVotoRequestStub;import org.junit.jupiter.api.Test;import org.junit.jupiter.api.extension.ExtendWith;import org.springframework.test.context.junit.jupiter.SpringExtension;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNull;@ExtendWith(SpringExtension.class)class SessaoVotoMapperTest {    @Test    void deveRetornarNullSeParametroNaoForInformado() {        assertNull(SessaoVotoMapper.mapToEntity(null));    }    @Test    void deveRetornarUmaEntidadeParaPersistencia() {        var entity = SessaoVotoMapper.mapToEntity(SessaoVotoRequestStub.sessaoVotoRequest());        assertEquals(entity, SessaoVotoEntityStub.sessaoVotoEntity());    }}